angular.module("httploading",[]).directive("httpLoading",function(){return{scope:{httploading:"="},restrict:"EA",template:'<div id="mask-loading" class="mask-loading" ng-if="httploading" style="background-color: rgba(0,0,0,0.3);">  <style type="text/css"> .mask-loading .loading-icon{-webkit-animation:rotate 1s linear infinite;-o-animation:rotate 1s linear infinite;animation:rotate 1s linear infinite;position:absolute;top:50%;left:50%;width:50px;height:50px;margin:-20px 0 0 -20px;border-width:5px;border-style:solid;border-color:#4b8de6 #4b8de6 #fff #fff;opacity:1;border-radius:25px}@-webkit-keyframes rotate{0%{-webkit-transform:rotate(0)}100%{-webkit-transform:rotate(360deg)}}@keyframes rotate{0%{transform:rotate(0)}100%{transform:rotate(360deg)}}.mask-loading{position:fixed;top:0;right:0;bottom:0;left:0;background:0;z-index:99999999}</style> <div class="loading-icon"></div>	</div>',replace:true,transclude:true,link:function(c,d,b,a){}}});